package Startarray;

public class Stringdemo3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "Anjali";
		for(int i=0; i<name.length(); i++)
		{
		char letter = name.charAt(i);
		System.out.print((char)(letter-32));
		}
	
		

	}

}
