package forloop_practice;

public class Looping_prgs {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Looping_prgs lp = new Looping_prgs();
//		lp.print_11111();
		lp.print_12345(); 

	}

	private void print_12345() {
		// TODO Auto-generated method stub
		for(int i=1;i<=5;i++) {
			System.out.print(i+" ");
		}
		
	}

	private void print_11111() {
		// TODO Auto-generated method stub
		for(int i=1; i<=5; i++) {
			System.out.print(1+" ");
		}
		
	}

}
